import React, { Component } from 'react';
import './App.css';
import { getSuggestions } from './mock-server'

class App extends Component{
  constructor() {
    super();
    this.state = {
      suggestions: [],
      inputValue: '',
    }
  }

  handleClick(e) {
    let value = e.target.value;

    getSuggestions(value).then(suggestions => {
      this.setState({
        suggestions: suggestions,
        inputValue: value,
      }) ;

      const matchingItem = suggestions.find(item => {
        return item.indexOf(value) !== -1;
      });

      const listItems = document.getElementById('suggestionItem');
      for(let i =0; i < listItems.length; i++) {
        if(listItems[i].outerText === matchingItem) {
          listItems[i].setAttribute('style', 'background-color: #E0E0E0');
        }
      }
    });
  }

  onExitingInputBox() {
     document.getElementsByTagName('ul').innerHTML = "";
  }

  selectItem(e, data) {
    this.setState({
      inputValue: data+ " ",
    });
    document.getElementById('searchBox').focus();
  }

  render() {
    const showSuggestions =  <div id="suggestionBox">
          {this.state.suggestions.map(item => <div id="suggestionItem" className="suggestionItem" key={item} onClick={(e) => this.selectItem(e, item)}>{item}</div>)}
      </div>

    return (
      <div className="app">
       <input name="search" type="text" value={this.state.inputValue} id="searchBox" onChange={(e) => this.handleClick(e)} onClick={(e) => this.handleClick(e)} onBlur={() => this.onExitingInputBox()}></input>
         {showSuggestions}
      </div>
    );
  }
}
export default App;
