import React, { Component } from 'react';
import './App.css';
import { getSuggestions } from './mock-server'

class App extends Component{
  constructor() {
    super();
    this.state = {
      suggestions: [],
      inputValue: '',
    }
  }

  handleClick(e) {
    console.log('fsaf', e.target.value);
    let value = document.getElementById('searchBox').value;
    getSuggestions(value).then(suggestions => {
      this.setState({
        suggestions: suggestions,
        inputValue: value,
      }) 
    });
  }

  selectItem(e, data) {
    this.setState({
      inputValue: data+ " ",
    })
  }

  render() {
    const showSuggestions =  <ul>
          {this.state.suggestions.map(item => <li key={item} onClick={(e) => this.selectItem(e, item)}>{item}</li>)}
      </ul>

    return (
      <div className="app">
       <input name="search" type="text" value={this.state.inputValue} id="searchBox" onKeyDown={(e) => this.handleClick(e)} onClick={(e) => this.handleClick(e)}></input>
       {showSuggestions}
      </div>
    );
  }
}
export default App;
