import React, { Component } from 'react';
import './App.css';
import { getSuggestions } from './mock-server'

class App extends Component{
  constructor() {
    super();
    this.state = {
      suggestions: [],
      inputValue: '',
    }
  }

  handleClick() {
    let value = document.getElementById('searchBox').value;
    getSuggestions(value).then(suggestions => {
      this.setState({
        suggestions: suggestions,
      }) 
    });
  }

  selectItem(e, data) {
    this.setState({
      inputValue: data+ " ",
    })
  }

  render() {
    const showSuggestions =  <ul>
          {this.state.suggestions.map(item => <li key={item} onClick={(e) => this.selectItem(e, item)}>{item}</li>)}
      </ul>

    return (
      <div className="app">
       <input value={this.state.inputValue} id="searchBox" onKeyDown={() => this.handleClick()}></input>
       {showSuggestions}
      </div>
    );
  }
}
export default App;
