import React, { Component } from 'react';
import './App.css';
import { getSuggestions } from './mock-server'

class App extends Component{
  constructor() {
    super();
    this.state = {
      suggestions: [],
      inputValue: '',
      select: false,
    }
  }

  handleClick(e) {
    let value = e.target.value;
    getSuggestions(value).then(suggestions => {
      this.setState({
        suggestions: suggestions,
        inputValue: value,
      }) 
    });

    const x = this.state.suggestions.find(item => {
      return item.indexOf(value) !== -1;
    });
    const y = document.getElementsByTagName('li')
    for(let i =0; i < y.length; i++) {
      if(y[i].outerText === x) {
        y[i].setAttribute('style', 'color: red');
      }
    }
  }

  selectItem(e, data) {
    this.setState({
      inputValue: data+ " ",
      select: true,
    });
    document.getElementById('searchBox').focus();
  }

  render() {
    const showSuggestions =  <ul>
          {this.state.suggestions.map(item => <li className="suggestionItem" key={item} onClick={(e) => this.selectItem(e, item)}>{item}</li>)}
      </ul>

    return (
      <div className="app">
       <input name="search" type="text" value={this.state.inputValue} id="searchBox" onChange={(e) => this.handleClick(e)} onClick={(e) => this.handleClick(e)}></input>
       {showSuggestions}
      </div>
    );
  }
}
export default App;
